import React from "react";
import * as firebase from "firebase/app";
import "firebase/auth";
import { UserContext } from "../../App";
import { useHistory, useLocation } from "react-router-dom";
import { Button, Card } from "react-bootstrap";
import { useContext } from "react";
import firebaseConfig from "./firebase.Config";
import google from "./google.png";

const Login = () => {
  const [loggedInUser, setLoggedInUser] = useContext(UserContext);

  const history = useHistory();
  const location = useLocation();
  const { from } = location.state || { from: { pathname: `/customer` } };

  if (firebase.apps.length === 0) {
    firebase.initializeApp(firebaseConfig);
  }

  const handleGoogleSignIn = () => {
    var provider = new firebase.auth.GoogleAuthProvider();
    firebase
      .auth()
      .signInWithPopup(provider)
      .then(function (result) {
        const { displayName, email } = result.user;
        const signedInUser = { name: displayName, email };
        setLoggedInUser(signedInUser);
        history.replace(from);
      })
      .catch(function (error) {
        const errorMessage = error.message;
        console.log(errorMessage);
      });
  };
  return (
    <div className="container p-3 bg-dark">
      
      <div style={{alignItem:"center"}}>
      <Card style={{ width: "25rem", height: "18rem", alignItem: "center", border:"1px solid gray" }}>
        <Card.Body>
          <div className="d-flex align-items-center" style={{ height: "3rem", width: "18rem", borderRadius:"1rem", backgroundColor:"white", border:"1px solid gray" }}>
            <img
              style={{ width: "2rem", height: "2rem", marginRight:"2rem" }}
              src={google}
              alt=""
            />
            <button
              
              style={{ height: "2rem", border: "none", width: "12rem",backgroundColor:"white" }}
              onClick={() => handleGoogleSignIn()}
            >
              Login
            </button>
          </div>
        </Card.Body>
      </Card>
      </div>
    </div>
  );
};

export default Login;
