import React from "react";
import * as firebase from "firebase/app";
import "firebase/auth";
import { UserContext } from "../../App";
import { useHistory, useLocation } from "react-router-dom";
import { Button, Card } from "react-bootstrap";
import { useContext } from "react";
import firebaseConfig from "./firebase.Config";
import google from "./google.png";

const Login = () => {
  const [loggedInUser, setLoggedInUser] = useContext(UserContext);

  const history = useHistory();
  const location = useLocation();
  const { from } = location.state || { from: { pathname: `/customer` } };

  if (firebase.apps.length === 0) {
    firebase.initializeApp(firebaseConfig);
  }

  const handleGoogleSignIn = () => {
    var provider = new firebase.auth.GoogleAuthProvider();
    firebase
      .auth()
      .signInWithPopup(provider)
      .then(function (result) {
        const { displayName, email } = result.user;
        const signedInUser = { name: displayName, email };
        setLoggedInUser(signedInUser);
        history.replace(from);
      })
      .catch(function (error) {
        const errorMessage = error.message;
        console.log(errorMessage);
      });
  };
  return (
    <div style={{ alignItems: "center" }}>
      
      <Card style={{ width: "22rem", height: "16rem", alignItem: "center" }}>
        <Card.Body>
          <div className="d-flex bg-light" style={{ height: "50px", width: "18rem", borderRadius:"1rem" }}>
            <img
              style={{ width: "50px", height: "50px" }}
              src={google}
              alt=""
            />
            <button
              
              style={{ height: "50px", border: "none", width: "18rem" }}
              onClick={() => handleGoogleSignIn()}
            >
              Login
            </button>
          </div>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Login;
