import React from "react";
import { Card, Image } from "react-bootstrap";






var skillUp = [
    {
        title:"Web & Mobile Design",
        text: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Aliquam molestiae praesentium perferendis maxime dolorem quod soluta tempore quisquam cumque reiciendis.",
        img:"https://i.ibb.co/ZBqF8pC/service1.png"
    },
    {
        title:"Graphic design",
        text: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Aliquam molestiae praesentium perferendis maxime dolorem quod soluta tempore quisquam cumque reiciendis.",
        img:"https://i.ibb.co/zmNSsxC/service2.png"
    },    {
        title:"Web development",
        text: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Aliquam molestiae praesentium perferendis maxime dolorem quod soluta tempore quisquam cumque reiciendis.",
        img:"https://i.ibb.co/hYpdXW7/service3.png"
    }
];

const SkillUp = () => {
  return (
    
        <div className="container row col-sm-12 d-flex justify-content-center mt-2 ml-2">
          <h3>Provide awesome <span style={{color:"#7ab259", textAlign:"center", height:"20rem"}}>Service</span></h3>
            {
            skillUp.map(skill => (<Card style={{ width: '18rem',alignItems:"center", align:"center", padding:"20px", marginLeft:"20px", marginTop:"1rem" }}>
            <Image style={{height:"50px", width:"50px"}} src={skill.img} />
            <Card.Body>
              <Card.Title>{skill.title}</Card.Title>
              <Card.Text>
               {skill.text}
              </Card.Text>
             
            </Card.Body>
          </Card>
            ))}
        
    </div>

  );
};

export default SkillUp;
